import './App.css';
import image from './images/GERADOR_DE.png'
import pptxgen from "pptxgenjs";


function App() {
  let pptx = new pptxgen();
let slide = pptx.addSlide();

slide.addText(
  "React Demo!",
  { x:1, y:0.5, w:'80%', h:1, fontSize:36, align:'center', fill:{ color:'D3E3F3' }, color:'008899' }
);

slide.addChart(
  pptx.ChartType.radar, dataChartRadar, { x:1.0, y:1.9, w:8, h:3 }
);

slide.addText(
  "PpptxGenJS version:",
  { x:0, y:5.3, w:'100%', h:0.33, align:'center', fill:{ color:'E1E1E1' }, color:'A1A1A1' }
);

pptx.writeFile({ fileName: 'pptxgenjs-demo-react.pptx' });
  return (
    <div>
        <div class="img-fluid">
            <img src={image} alt='imagem' />
      </div>
    <div class="area-texto">
        <textarea id="area-texto" name="area-vsl" rows="30" cols="150">
        apenas testes
        </textarea>
    </div>
    <div class="area-buttons">
        <button>Gerar VSL</button> 
    </div>
    <footer class="footter-content">Crisgay.inc@2022</footer>
    </div>
  );
}

export default App;
