import './App.css';
import image from './images/GERADOR_DE.png'
import pptxgen from "pptxgenjs";


function App() {
let pres = new pptxgen();

let slide = pres.addSlide();

slide.addText("Hello World from PptxGenJS...", {
    x: 1.5,
    y: 1.5,
    color: "363636",
    fill: { color: "F1F1F1" },
    align: pres.AlignH.center,
});

pres.writeFile({ fileName: "Sample Presentation.pptx" });
  return (
    <div>
        <div class="img-fluid">
            <img src={image} alt='imagem' />
      </div>
    <div class="area-texto">
        <textarea id="area-texto" name="area-vsl" rows="30" cols="150">
        apenas testes
        </textarea>
    </div>
    <div class="area-buttons">
        <button>Gerar VSL</button> 
    </div>
    <footer class="footter-content">Crisgay.inc@2022</footer>
    </div>
  );
}

export default App;
