import './App.css';
import image from './images/GERADOR_DE.png'
import pptxgen from "pptxgenjs";
import React, { useState } from 'react';

function App() {

  const [text, useText] = useState('')

  const handleChange = ({ target }) => {
    const { txt } = target;
    useText({
      [txt]: target.value,
    });
  }

  const generate = () => {
    let pptx = new pptxgen()

    let slide = pptx.addSlide();
    slide.addText(text)


    pptx.writeFile()
}
  return (
    <div>
        <div className="img-fluid">
            <img src={image} alt='imagem' />
      </div>
    <div className="area-texto">
        <textarea id="area-texto" name="area-vsl" onChange={handleChange} rows={30} cols={150} >
          {useText(txt)}
        </textarea>
    </div>
    <div className="area-buttons">
        <button onClick={generate}>Gerar VSL</button> 
    </div>
    <footer className="footter-content">Crisgay.inc@2022</footer>
    </div>
  );
}

export default App;
